import type { CSSVarFunction } from '../typeUtils';
type RGBType = `rgb(${CSSVarFunction})`;
type RGBAType = `rgba(${CSSVarFunction}, ${number})`;
type ColorMixType = `color-mix(in srgb, ${string} ${string}, ${string})`;
declare const rgb: (rgb: CSSVarFunction) => RGBType;
declare const rgba: (rgb: CSSVarFunction, alpha: number) => RGBAType;
declare const colorMix: (mixColor: string, mixPercent: string, baseColor: string) => ColorMixType;
export type { RGBType, RGBAType };
export { colorMix, rgb, rgba };
