import { CurrentKeyDetail, Item } from '../UNSAFE_Collection';
export type CurrentItemProps<K> = {
    /**
     * The key of the item that currently have keyboard focus. Ignored if the current item is not
     * currently in the viewport.
     */
    currentItemOverride?: Item<K>;
    /**
     * Callback function to handle when current focused item has changed.
     */
    onPersistCurrentItem?: (detail: CurrentKeyDetail<K>) => void;
};
