import { ComponentChild, ComponentChildren } from 'preact';
/**
 * Type of the context object passed pass to ContextMenuConfig
 */
export type TabBarItemContext<K> = {
    itemKey: K;
};
type ContextMenuContext<M extends string = string> = {
    allMenuItems?: Record<M, ComponentChild>;
    defaultMenuItems?: ComponentChild;
};
export type TabBarItemContextMenuContext<K> = ContextMenuContext & TabBarItemContext<K>;
/**
 * Type of the context object passed pass to ContextMenuConfig
 */
export type TabBarContextMenuConfig<K> = {
    itemsRenderer: (context: TabBarItemContextMenuContext<K>) => ComponentChildren;
    accessibleLabel?: string;
};
export {};
