import { By, Key } from 'selenium-webdriver';
import { OjButtonBase } from './OjButtonBase';

/**
 * The component WebElement for [oj-button](../../jsdocs/oj.ojButton.html).
 * Do not instantiate this class directly, instead, use
 * [ojButton](../functions/elements.ojButton-1.html).
 */

/**
 * The component WebElement for [oj-button](../../jsdocs/oj.ojButton.html).
 * Do not instantiate this class directly, instead, use
 * [ojButton](../functions/elements.ojButton-1.html).
 *  <h4>Migration</h4>
 *  When you migrate a page from using [oj-button](../../jsdocs/oj.ojButton.html) to [oj-c-button](../../jsdocs/oj-c.Button.html) you will also need to migrate your WebDriver tests from OjButton to ButtonWebElement. To do that you will need to revise type and locator instances as follows
 *  </br><b>Type:</b> from OjButton to [ButtonWebElement](../../cpwdtsdoc/classes/ButtonWebElement.html)
 *  </br><b>Locator:</b> from [ojButton](../functions/elements.ojButton-1.html) to [findButton](../../cpwdtsdoc/functions/findButton.html)
 *  </br>For more details on migrating from legacy to core pack please see component's [migration section](../../jsdocs/oj.ojButton.html#migration-section)
 */

export class OjButton extends OjButtonBase {
  /**
   * Perform a click on the button
   */
  doAction() {
    return this.click();
  }

  /**
   * Perform a context-click on the button
   */
  async doContextClick() {
    let button = await this.findElement(By.css('button'));
    await button.sendKeys(Key.SHIFT, Key.F10);
  }

  /**
   * Perform a click on the button
   */
  async click() {
    // Find the <button> element to click so that it can receive focus
    const button = await this.findElement(By.css('button'));
    return button.click();
  }
}
