import { By } from 'selenium-webdriver';
import { DriverLike, register } from '../../';
import { OjButtonsetOne } from './OjButtonsetOne';
export { OjButtonsetOne };

/**
 * Retrieve an instance of [OjButtonsetOne](../classes/elements.OjButtonsetOne.html).
 * @example
 * ```javascript
 * import { ojButtonsetOne } from '@oracle/oraclejet-webdriver/elements';
 * const el = await ojButtonsetOne(driver, By.id('my-oj-buttonset-one'));
 * ```
 * @param driver A WebDriver/WebElement instance from where the element will be
 * searched. If WebDriver is passed, the element will be searched globally in the
 * document. If WebElement is passed, the search will be relative to this element.
 * @param by The locator with which to find the element
 */
export async function ojButtonsetOne(driver: DriverLike, by: By): Promise<OjButtonsetOne> {
  const webEl = await driver.findElement(by);
  // Check that the element is of type OjButtonsetOne
  if (!(webEl instanceof OjButtonsetOne)) {
    const tagName = await webEl.getTagName();
    let supplemental = '';
    if (webEl.constructor.name === 'OjButtonsetOne') {
      supplemental = 'You likely have multiple installations of this package.';
    }
    throw Error(
      `ojButtonsetOne(${by}) created ${webEl.constructor.name} for <${tagName}>, but expected ${OjButtonsetOne.name}. ${supplemental}`
    );
  }
  return webEl;
}

register('oj-buttonset-one', OjButtonsetOne);
