import { Locator } from 'selenium-webdriver';
import { OjConveyorBeltBase } from './OjConveyorBeltBase';

/**
 * The component WebElement for [oj-conveyor-belt](../../jsdocs/oj.ojConveyorBelt.html).
 * Do not instantiate this class directly, instead, use
 * [ojConveyorBelt](../functions/elements.ojConveyorBelt-1.html).
 */

/**
 * The component WebElement for [oj-conveyor-belt](../../jsdocs/oj.ojConveyorBelt.html).
 * Do not instantiate this class directly, instead, use
 * [ojConveyorBelt](../functions/elements.ojConveyorBelt-1.html).
 *  <h4>Migration</h4>
 *  When you migrate a page from using [oj-conveyor-belt](../../jsdocs/oj.ojConveyorBelt.html) to [oj-c-conveyor-belt](../../jsdocs/oj-c.ConveyorBelt.html) you will also need to migrate your WebDriver tests from OjConveyorBelt to ConveyorBeltWebElement. To do that you will need to revise type and locator instances as follows
 *  </br><b>Type:</b> from OjConveyorBelt to [ConveyorBeltWebElement](../../cpwdtsdoc/classes/ConveyorBeltWebElement.html)
 *  </br><b>Locator:</b> from [ojConveyorBelt](../functions/elements.ojConveyorBelt-1.html) to [findConveyorBelt](../../cpwdtsdoc/functions/findConveyorBelt.html)
 *  </br>For more details on migrating from legacy to core pack please see component's [migration section](../../jsdocs/oj.ojConveyorBelt.html#migration-section)
 */
export class OjConveyorBelt extends OjConveyorBeltBase {
  // Put overrides here

  /**
   * Scroll the conveyor belt, so that the conveyor belt's element (button, tab bar) of interest is in the view
   *
   * @param {Locator} locator The lookup to pass into findElement
   */
  async scrollElementIntoView(locator: Locator): Promise<void> {
    await this.whenReady();
    let el = await this.findElement(locator);
    return await this.getDriver().executeScript(
      (cb: any, el: Element) => cb.scrollElementIntoView(el),
      this,
      el
    );
  }
}
