import { By } from 'selenium-webdriver';
import { DriverLike, register } from '../../';
import { OjDatePicker } from './OjDatePicker';
export { OjDatePicker };

/**
 * Retrieve an instance of [OjDatePicker](../classes/elements.OjDatePicker.html).
 * @example
 * ```javascript
 * import { ojDatePicker } from '@oracle/oraclejet-webdriver/elements';
 * const el = await ojDatePicker(driver, By.id('my-oj-date-picker'));
 * ```
 * @param driver A WebDriver/WebElement instance from where the element will be
 * searched. If WebDriver is passed, the element will be searched globally in the
 * document. If WebElement is passed, the search will be relative to this element.
 * @param by The locator with which to find the element
 */
export async function ojDatePicker(driver: DriverLike, by: By): Promise<OjDatePicker> {
  const webEl = await driver.findElement(by);
  // Check that the element is of type OjDatePicker
  if (!(webEl instanceof OjDatePicker)) {
    const tagName = await webEl.getTagName();
    let supplemental = '';
    if (webEl.constructor.name === 'OjDatePicker') {
      supplemental = 'You likely have multiple installations of this package.';
    }
    throw Error(
      `ojDatePicker(${by}) created ${webEl.constructor.name} for <${tagName}>, but expected ${OjDatePicker.name}. ${supplemental}`
    );
  }
  return webEl;
}

register('oj-date-picker', OjDatePicker);
