import { OjFilmStripBase } from './OjFilmStripBase';

/**
 * The component WebElement for [oj-film-strip](../../jsdocs/oj.ojFilmStrip.html).
 * Do not instantiate this class directly, instead, use
 * [ojFilmStrip](../functions/elements.ojFilmStrip-1.html).
 */
export class OjFilmStrip extends OjFilmStripBase {
  /**
   * Sets the value of <code>currentItem</code> property to a given index.
   * Specifies the child item whose logical page should be displayed.
   * @param index The index value to set for <code>currentItem</code>
   *
   */
  public changeCurrentItemByIndex(index: number): Promise<void> {
    return this.setProperty('currentItem', { index: index });
  }

  /**
   * Sets the value of <code>currentItem</code> property to a given id.
   * Specifies the child item whose logical page should be displayed.
   * @param id The id value to set for <code>currentItem</code>
   *
   */
  public changeCurrentItemById(id: string): Promise<void> {
    return this.setProperty('currentItem', { id: id });
  }
}
