import { By } from 'selenium-webdriver';
import { DriverLike, register } from '../../';
import { OjInputDateTime } from './OjInputDateTime';
export { OjInputDateTime };

/**
 * Retrieve an instance of [OjInputDateTime](../classes/elements.OjInputDateTime.html).
 * @example
 * ```javascript
 * import { ojInputDateTime } from '@oracle/oraclejet-webdriver/elements';
 * const el = await ojInputDateTime(driver, By.id('my-oj-input-date-time'));
 * ```
 * @param driver A WebDriver/WebElement instance from where the element will be
 * searched. If WebDriver is passed, the element will be searched globally in the
 * document. If WebElement is passed, the search will be relative to this element.
 * @param by The locator with which to find the element
 */
export async function ojInputDateTime(driver: DriverLike, by: By): Promise<OjInputDateTime> {
  const webEl = await driver.findElement(by);
  // Check that the element is of type OjInputDateTime
  if (!(webEl instanceof OjInputDateTime)) {
    const tagName = await webEl.getTagName();
    let supplemental = '';
    if (webEl.constructor.name === 'OjInputDateTime') {
      supplemental = 'You likely have multiple installations of this package.';
    }
    throw Error(
      `ojInputDateTime(${by}) created ${webEl.constructor.name} for <${tagName}>, but expected ${OjInputDateTime.name}. ${supplemental}`
    );
  }
  return webEl;
}

register('oj-input-date-time', OjInputDateTime);
