import { By } from 'selenium-webdriver';
import { DriverLike, register } from '../../';
import { OjInputTime } from './OjInputTime';
export { OjInputTime };

/**
 * Retrieve an instance of [OjInputTime](../classes/elements.OjInputTime.html).
 * @example
 * ```javascript
 * import { ojInputTime } from '@oracle/oraclejet-webdriver/elements';
 * const el = await ojInputTime(driver, By.id('my-oj-input-time'));
 * ```
 * @param driver A WebDriver/WebElement instance from where the element will be
 * searched. If WebDriver is passed, the element will be searched globally in the
 * document. If WebElement is passed, the search will be relative to this element.
 * @param by The locator with which to find the element
 */
export async function ojInputTime(driver: DriverLike, by: By): Promise<OjInputTime> {
  const webEl = await driver.findElement(by);
  // Check that the element is of type OjInputTime
  if (!(webEl instanceof OjInputTime)) {
    const tagName = await webEl.getTagName();
    let supplemental = '';
    if (webEl.constructor.name === 'OjInputTime') {
      supplemental = 'You likely have multiple installations of this package.';
    }
    throw Error(
      `ojInputTime(${by}) created ${webEl.constructor.name} for <${tagName}>, but expected ${OjInputTime.name}. ${supplemental}`
    );
  }
  return webEl;
}

register('oj-input-time', OjInputTime);
