import { By } from 'selenium-webdriver';
import { DriverLike, register } from '../../';
import { OjListView } from './OjListView';
export { OjListView };

/**
 * Retrieve an instance of [OjListView](../classes/elements.OjListView.html).
 * @example
 * ```javascript
 * import { ojListView } from '@oracle/oraclejet-webdriver/elements';
 * const el = await ojListView(driver, By.id('my-oj-list-view'));
 * ```
 * @param driver A WebDriver/WebElement instance from where the element will be
 * searched. If WebDriver is passed, the element will be searched globally in the
 * document. If WebElement is passed, the search will be relative to this element.
 * @param by The locator with which to find the element
 */
export async function ojListView(driver: DriverLike, by: By): Promise<OjListView> {
  const webEl = await driver.findElement(by);
  // Check that the element is of type OjListView
  if (!(webEl instanceof OjListView)) {
    const tagName = await webEl.getTagName();
    let supplemental = '';
    if (webEl.constructor.name === 'OjListView') {
      supplemental = 'You likely have multiple installations of this package.';
    }
    throw Error(
      `ojListView(${by}) created ${webEl.constructor.name} for <${tagName}>, but expected ${OjListView.name}. ${supplemental}`
    );
  }
  return webEl;
}

register('oj-list-view', OjListView);
