import { By } from 'selenium-webdriver';
import { OjMenuButtonBase } from './OjMenuButtonBase';

/**
 * The component WebElement for [oj-menu-button](../../jsdocs/oj.ojMenuButton.html).
 * Do not instantiate this class directly, instead, use
 * [ojMenuButton](../functions/elements.ojMenuButton-1.html).
 */
export class OjMenuButton extends OjMenuButtonBase {
  /**
   * Perform a click on the button
   */
  async doAction() {
    await this.whenReady();
    const b = await this.findElement(By.css('button'));
    await b.click();
  }
}
