import { OjRangeSliderBase, Value } from './OjRangeSliderBase';

/**
 * The component WebElement for [oj-range-slider](../../jsdocs/oj.ojRangeSlider.html).
 * Do not instantiate this class directly, instead, use
 * [ojRangeSlider](../functions/elements.ojRangeSlider-1.html).
 */
export class OjRangeSlider extends OjRangeSliderBase {
  // Put overrides here

  /**
   * Sets the value of the <code>value</code> property.
   * The numerical value of the range-slider.
   * @param value The value to set for <code>value</code>
   */
  async changeValue(value: Value | null) {
    // Only mutate if not disabled
    const disabled = await this.getDisabled();
    if (!disabled) {
      // Call focus() on the root element
      await this.getDriver().executeScript((el: HTMLElement) => el.focus(), this);
      return this.setProperty<Value | null>('value', value);
    }
  }
}
