import { By } from 'selenium-webdriver';
import { DriverLike, register } from '../../';
import { OjRangeSlider } from './OjRangeSlider';
export { OjRangeSlider };

/**
 * Retrieve an instance of [OjRangeSlider](../classes/elements.OjRangeSlider.html).
 * @example
 * ```javascript
 * import { ojRangeSlider } from '@oracle/oraclejet-webdriver/elements';
 * const el = await ojRangeSlider(driver, By.id('my-oj-range-slider'));
 * ```
 * @param driver A WebDriver/WebElement instance from where the element will be
 * searched. If WebDriver is passed, the element will be searched globally in the
 * document. If WebElement is passed, the search will be relative to this element.
 * @param by The locator with which to find the element
 */
export async function ojRangeSlider(driver: DriverLike, by: By): Promise<OjRangeSlider> {
  const webEl = await driver.findElement(by);
  // Check that the element is of type OjRangeSlider
  if (!(webEl instanceof OjRangeSlider)) {
    const tagName = await webEl.getTagName();
    let supplemental = '';
    if (webEl.constructor.name === 'OjRangeSlider') {
      supplemental = 'You likely have multiple installations of this package.';
    }
    throw Error(
      `ojRangeSlider(${by}) created ${webEl.constructor.name} for <${tagName}>, but expected ${OjRangeSlider.name}. ${supplemental}`
    );
  }
  return webEl;
}

register('oj-range-slider', OjRangeSlider);
