import { By } from 'selenium-webdriver';
import { DriverLike, register } from '../../';
import { OjRefresher } from './OjRefresher';
export { OjRefresher };

/**
 * Retrieve an instance of [OjRefresher](../classes/elements.OjRefresher.html).
 * @example
 * ```javascript
 * import { ojRefresher } from '@oracle/oraclejet-webdriver/elements';
 * const el = await ojRefresher(driver, By.id('my-oj-refresher'));
 * ```
 * @param driver A WebDriver/WebElement instance from where the element will be
 * searched. If WebDriver is passed, the element will be searched globally in the
 * document. If WebElement is passed, the search will be relative to this element.
 * @param by The locator with which to find the element
 */
export async function ojRefresher(driver: DriverLike, by: By): Promise<OjRefresher> {
  const webEl = await driver.findElement(by);
  // Check that the element is of type OjRefresher
  if (!(webEl instanceof OjRefresher)) {
    const tagName = await webEl.getTagName();
    let supplemental = '';
    if (webEl.constructor.name === 'OjRefresher') {
      supplemental = 'You likely have multiple installations of this package.';
    }
    throw Error(
      `ojRefresher(${by}) created ${webEl.constructor.name} for <${tagName}>, but expected ${OjRefresher.name}. ${supplemental}`
    );
  }
  return webEl;
}

register('oj-refresher', OjRefresher);
