import { OjSelectorBase } from './OjSelectorBase';
import { By } from 'selenium-webdriver';

/**
 * The component WebElement for [oj-selector](../../jsdocs/oj.ojSelector.html).
 * Do not instantiate this class directly, instead, use
 * [ojSelector](../functions/elements.ojSelector-1.html).
 */
export class OjSelector extends OjSelectorBase {
  /**
   * Gets the selected value of the selector.
   * @return boolean selection checked state.
   */
  async isSelected(): Promise<boolean> {
    let checkbox = await this.findElements(By.xpath('.//input'));
    return checkbox[0].isSelected();
  }
}
