import { By } from 'selenium-webdriver';
import { DriverLike, register } from '../../';
import { OjTimeAxis } from './OjTimeAxis';
export { OjTimeAxis };

/**
 * Retrieve an instance of [OjTimeAxis](../classes/elements.OjTimeAxis.html).
 * @example
 * ```javascript
 * import { ojTimeAxis } from '@oracle/oraclejet-webdriver/elements';
 * const el = await ojTimeAxis(driver, By.id('my-oj-time-axis'));
 * ```
 * @param driver A WebDriver/WebElement instance from where the element will be
 * searched. If WebDriver is passed, the element will be searched globally in the
 * document. If WebElement is passed, the search will be relative to this element.
 * @param by The locator with which to find the element
 */
export async function ojTimeAxis(driver: DriverLike, by: By): Promise<OjTimeAxis> {
  const webEl = await driver.findElement(by);
  // Check that the element is of type OjTimeAxis
  if (!(webEl instanceof OjTimeAxis)) {
    const tagName = await webEl.getTagName();
    let supplemental = '';
    if (webEl.constructor.name === 'OjTimeAxis') {
      supplemental = 'You likely have multiple installations of this package.';
    }
    throw Error(
      `ojTimeAxis(${by}) created ${webEl.constructor.name} for <${tagName}>, but expected ${OjTimeAxis.name}. ${supplemental}`
    );
  }
  return webEl;
}

register('oj-time-axis', OjTimeAxis);
